import pandas as pdimport matplotlib.pyplot as plt## air pollution plotting## city : austin## category : no2# import all the date needed for ploting austin air pollution (no2)austin_2020_no2 = pd.read_csv('/Users/yf/python-mid-data-demo/air_pollution/austin/no2/austin_2020_no2.csv')austin_2019_no2 = pd.read_csv('/Users/yf/python-mid-data-demo/air_pollution/austin/no2/austin_2019_no2.csv')austin_2018_no2 = pd.read_csv('/Users/yf/python-mid-data-demo/air_pollution/austin/no2/austin_2018_no2.csv')austin_2020_no2_unselected = austin_2020_no2.loc[:, ['Date', 'Daily Max 1-hour NO2 Concentration','Site ID']]austin_2020_no2_unselected = austin_2020_no2_unselected[austin_2020_no2_unselected['Site ID'] == 484531068] austin_2019_no2_unselected = austin_2019_no2.loc[:, ['Date', 'Daily Max 1-hour NO2 Concentration','Site ID']]austin_2019_no2_unselected = austin_2019_no2_unselected[austin_2019_no2_unselected['Site ID'] == 484531068] austin_2018_no2_unselected = austin_2018_no2.loc[:, ['Date', 'Daily Max 1-hour NO2 Concentration','Site ID']]austin_2018_no2_unselected = austin_2018_no2_unselected[austin_2018_no2_unselected['Site ID'] == 484531068] austin_2020_no2_date = austin_2020_no2_unselected['Date'].values.tolist()austin_2020_no2_concentration = austin_2020_no2_unselected['Daily Max 1-hour NO2 Concentration'].values.tolist()austin_2019_no2_date = austin_2019_no2_unselected['Date'].values.tolist()austin_2019_no2_concentration = austin_2019_no2_unselected['Daily Max 1-hour NO2 Concentration'].values.tolist()austin_2018_no2_date = austin_2018_no2_unselected['Date'].values.tolist()austin_2018_no2_concentration = austin_2018_no2_unselected['Daily Max 1-hour NO2 Concentration'].values.tolist()austin_no2_plot = plt.figure()ax = plt.gca()ax.spines['right'].set_color('none')ax.spines['top'].set_color('none')plt.subplot(3, 1, 1)plt.plot(austin_2018_no2_date, austin_2018_no2_concentration, c = '#B0C4DE')plt.xticks(())plt.title('2018')plt.subplot(3, 1, 2)plt.plot(austin_2019_no2_date, austin_2019_no2_concentration, c = '#6495ED')plt.xticks(())plt.title('2019')plt.subplot(3, 1, 3)plt.plot(austin_2020_no2_date, austin_2020_no2_concentration)plt.xticks(())plt.title('2020')plt.suptitle('2018-2020 Austin Air Pollutuon - NO2 \n x axis - Date \n y axis - Daily Max 1-hour NO2 Concentration',              fontsize='large')austin_no2_plot.tight_layout()plt.show()## air pollution plotting## city : austin## catagory : pm10# import all the date needed for ploting austin air pollution (pm10)austin_2020_pm10 = pd.read_csv('/Users/yf/python-mid-data-demo/air_pollution/austin/pm10/austin_2020_pm10.csv')austin_2019_pm10 = pd.read_csv('/Users/yf/python-mid-data-demo/air_pollution/austin/pm10/austin_2019_pm10.csv')austin_2018_pm10 = pd.read_csv('/Users/yf/python-mid-data-demo/air_pollution/austin/pm10/austin_2018_pm10.csv')austin_2020_pm10_unselected = austin_2020_pm10.loc[:, ['Date', 'Daily Mean PM10 Concentration','Site ID']]austin_2020_pm10_unselected = austin_2020_pm10_unselected[austin_2020_pm10_unselected['Site ID'] == 484530021] austin_2019_pm10_unselected = austin_2019_pm10.loc[:, ['Date', 'Daily Mean PM10 Concentration','Site ID']]austin_2019_pm10_unselected = austin_2019_pm10_unselected[austin_2019_pm10_unselected['Site ID'] == 484530021] austin_2018_pm10_unselected = austin_2018_pm10.loc[:, ['Date', 'Daily Mean PM10 Concentration','Site ID']]austin_2018_pm10_unselected = austin_2018_pm10_unselected[austin_2018_pm10_unselected['Site ID'] == 484530021] austin_2020_pm10_date = austin_2020_pm10_unselected['Date'].values.tolist()austin_2020_pm10_concentration = austin_2020_pm10_unselected['Daily Mean PM10 Concentration'].values.tolist()austin_2019_pm10_date = austin_2019_pm10_unselected['Date'].values.tolist()austin_2019_pm10_concentration = austin_2019_pm10_unselected['Daily Mean PM10 Concentration'].values.tolist()austin_2018_pm10_date = austin_2018_pm10_unselected['Date']